import React from "react";

export default function StarsRating(props) {
  const { rateSt } = props;

  let showStars = (rateSt) => {
    let flooredStars = Math.floor(rateSt);
    const starIcon = '<i class="fa-solid fa-star" style="color: #FFD43B;"></i>';
    const starsString = Array.from(
      { length: flooredStars },
      () => starIcon
    ).join("");
    return starsString;
  };

  showStars(rateSt);


}
