import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom/cjs/react-router-dom.min";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { chLoader } from "../store/action/loaderAction";
import Loader from "../Components/loader";
import BtnsCo from "../Components/Btns";
import StarsRating from "../Components/rate";
import Image from "react-bootstrap/Image";
import Carousel from "react-bootstrap/Carousel";

export default function SpecficProd() {
  const loader = useSelector((state) => state.loader.onLoad);
  const dispatch = useDispatch();
  const [speProducts, setSpeProduct] = useState([]);
  const { prodId } = useParams();
  let sessionLogin = JSON.parse(sessionStorage.getItem("login") || "[]");

  useEffect(() => {
    const fetchData = async () => {
      try {
        dispatch(chLoader(true));

        const response = await axios.get(
          `https://dummyjson.com/products/${prodId}`
        );
        setSpeProduct(response.data);
      } catch (error) {
        console.error("Error fetching data:", error);
      } finally {
        dispatch(chLoader(false)); 
      }
    };


    fetchData();
  }, [prodId, dispatch]);

  console.log("test");
  console.log(speProducts);
  console.log(loader);

  return (
    <>
      {loader ? (
        <div className="d-flex justify-content-center align-items-center vh-100">
          <Loader />
        </div>
      ) : (
        <>
          <h2>{speProducts.title}</h2>
          <p>{speProducts.category}</p>
          <div className="pt-3 d-flex container justify-content-center gap-3 flex-wrap">
            <Carousel fade>
              {speProducts.images &&
                speProducts.images.map((image, idx) => (
                  <Carousel.Item key={idx}>
                    <Image src={image} width="300" height="300" rounded />
                  </Carousel.Item>
                ))}
            </Carousel>
            <p>{speProducts.description}</p>
            <p>Stock: {speProducts.stock}</p>
            <p>Price: {speProducts.price}$</p>
          </div>
        </>
      )}
    </>
  );
}
