import React from "react";

export default function StarsRating(props) {
  const { rateSt } = props;

  let showEmptyStars = (rateSt) => {
    let flooredStars = Math.floor(rateSt);
    let emptyStars = 5 - flooredStars;
    const starIcon = '<i class="fa-light fa-star" style="color: #FFD43B;"></i>';



};

  let showStars = (rateSt) => {
    let flooredStars = Math.floor(rateSt);
    const starIcon = '<i class="fa-solid fa-star" style="color: #FFD43B;"></i>';
    const starsString = Array.from(
      { length: flooredStars },
      () => starIcon
    ).join("");
    return starsString;
  };

  const starsString = showStars(rateSt);

  return <div dangerouslySetInnerHTML={{ __html: starsString }}></div>;
}
