import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import BtnsCo from "../Components/Btns";
import { useDispatch, useSelector } from "react-redux";
import { chLoader } from "../store/action/loaderAction";
import Loader from "../Components/loader";
import { Link } from "react-router-dom";
import StarsRating from "../Components/rate";
import "../Pages/product.css";
import { addToFav } from "../store/action/action";
import { addToCart } from "../store/action/AddToCartAction";
import Offcanvas from "react-bootstrap/Offcanvas";
import SearchBar from "../Components/SearchBar";
import Pagination from "react-bootstrap/Pagination";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

function ProductPage() {
  const [products, setProduct] = useState([]);
  const [cate, setCate] = useState([]);
  const loader = useSelector((state) => state.loader.onLoad);
  const dispatch = useDispatch();
  let sessionLogin = JSON.parse(sessionStorage.getItem("login") || "[]");
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [currentPage, setCurrentPage] = useState(1);
  const productsPerPage = 8;

  const indexOfLastProduct = currentPage * productsPerPage;
  const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
  const currentProducts = products.slice(indexOfFirstProduct, indexOfLastProduct);

  const totalPages = Math.ceil(products.length / productsPerPage);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  useEffect(() => {
    axios
      .get("https://dummyjson.com/products?limit=100")
      .then((res) => {
        setTimeout(() => {
          dispatch(chLoader(false));
        }, 2000);
        setProduct(res.data.products);
      })
      .catch((rej) => {
        console.log("err");
      });
  }, []);

  useEffect(() => {
    axios
      .get(`https://dummyjson.com/products/categories`)
      .then((res) => {
        setTimeout(() => {
          dispatch(chLoader(false));
        }, 2000);
        setCate(res.data);
      })
      .catch((rej) => {
        console.log("err");
      });
  }, []);

  const addtoFave = (product) => {
    console.log(product);
    dispatch(addToFav(product));
  };

  const addtoCarti = (product) => {
    console.log(product);
    dispatch(addToCart(product));
  };

  return (
    <>
      {loader ? (
        <>
          <div className="d-flex justify-content-center align-items-center vh-100">
            <Loader />
          </div>
        </>
      ) : (
        <>
          <div className="container d-flex gap-3">
            <BtnsCo btnText="Filter" btnCo="warning" btnAct={handleShow} />
            <SearchBar />
            <Offcanvas show={show} onHide={handleClose}>
              <Offcanvas.Header closeButton>
                <Offcanvas.Title>Categories</Offcanvas.Title>
              </Offcanvas.Header>
              <Offcanvas.Body>
                <div className="d-flex flex-column gap-2" style={{ width: "auto" }}>
                  {cate.map((category) => (
                    <div key={category}>
                      <Link onClick={() => dispatch(chLoader(true))} to={`/nameC/${category}`} className="link-1">
                        {category}
                      </Link>
                    </div>
                  ))}
                </div>
              </Offcanvas.Body>
            </Offcanvas>
          </div>
          <Container fluid>
            <Row className="pt-3">
              <Col xs={12} lg={8} className="d-flex justify-content-center gap-3 flex-wrap">
                {currentProducts.map((product) => (
                  <Card key={product.id} style={{ width: "20rem" }}>
                    <Card.Img style={{ width: "100%", height: "180px" }} variant="top" src={product.images[1]} />
                    <Card.Body>
                      <Card.Title>{product.title}</Card.Title>
                      <Card.Text>{product.description}</Card.Text>
                    </Card.Body>
                    <div className="d-flex gap-3 p-3 flex-column">
                      {sessionLogin && sessionLogin.length > 0 && (
                        <BtnsCo
                          btnAct={() => addtoCarti(product)}
                          btnStyle="bg-black border-0"
                          btnLogo="fa-solid fa-cart-shopping"
                          btnText="Add To Cart"
                        />
                      )}
                      <Link to={`/prods/${product.id}`} onClick={() => dispatch(chLoader(true))} className="link-1">
                        <p>View Details</p>
                      </Link>
                      <div className="d-flex justify-content-center gap-3">
                        {sessionLogin && sessionLogin.length > 0 && (
                          <i
                            onClick={() => addtoFave(product)}
                            className="fa-regular fa-heart"
                            style={{ color: "#FF0000" }}
                          ></i>
                        )}
                        <div>
                          <StarsRating rateSt={product.rating} />
                        </div>
                      </div>
                    </div>
                  </Card>
                ))}
              </Col>
              <Col xs={12} lg={4} className="d-flex justify-content-center">
                <Pagination>
                  {Array.from({ length: totalPages }).map((_, index) => (
                    <Pagination.Item
                      key={index + 1}
                      active={index + 1 === currentPage}
                      onClick={() => paginate(index + 1)}
                    >
                      {index + 1}
                    </Pagination.Item>
                  ))}
                </Pagination>
              </Col>
            </Row>
          </Container>
        </>
      )}
    </>
  );
}

export default ProductPage;
