import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom/cjs/react-router-dom.min";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { chLoader } from "../store/action/loaderAction";
import Card from "react-bootstrap/Card";
import Loader from "../Components/loader";
import BtnsCo from "../Components/Btns";
import StarsRating from "../Components/rate";
import Image from "react-bootstrap/Image";
import Carousel from "react-bootstrap/Carousel";
import Figure from "react-bootstrap/Figure";

export default function SpecficProd() {
  const loader = useSelector((state) => state.loader.onLoad);
  const dispatch = useDispatch();
  const [speProducts, setSpeProduct] = useState([]);
  const { prodId } = useParams();
  let sessionLogin = JSON.parse(sessionStorage.getItem("login") || "[]");

  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  useEffect(() => {
    axios
      .get(`https://dummyjson.com/products/${prodId}`)
      .then((res) => {
        setTimeout(() => {
          dispatch(chLoader(false));
        }, 2000);
        setSpeProduct(res.data);
      })
      .catch((rej) => {
        console.log("err");
      });
  }, []);

  return (
    <>
      {loader ? (
        <>
          <div className="d-flex justify-content-center align-items-center vh-100">
            <Loader />
          </div>
        </>
      ) : (
        <>
          <div className="container d-flex justify-content-center align-items-center  flex-column">
            <div>
              <h2>{speProducts.title}</h2>
              <p>{speProducts.category}</p>

              <Carousel fade>
                <Carousel.Item>
                  <Image
                    src={speProducts.images[0]}
                    width="400"
                    height="400"
                    rounded
                  />
                </Carousel.Item>
                <Carousel.Item>
                  <Image
                    src={speProducts.images[1]}
                    width="400"
                    height="400"
                    rounded
                  />
                </Carousel.Item>
                <Carousel.Item>
                  <Image
                    src={speProducts.images[2]}
                    width="400"
                    height="400"
                    rounded
                  />
                </Carousel.Item>
              </Carousel>
            </div>
            <p>{speProducts.description}</p>
            <div className="pt-3 d-flex container justify-content-center gap-3 flex-wrap ">
              {speProducts.stock > 0 && (
                <p style={{ color: "green" }}>Stock : {speProducts.stock}</p>
              )}
              {speProducts.stock < 0 && (
                <p style={{ color: "green" }}>Stock : {speProducts.stock}</p>
              )}
              <p> price : {speProducts.price}$</p>
              <p> Brand : {speProducts.brand}</p>
            </div>
          </div>
        </>
      )}
    </>
  );
}
