import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom/cjs/react-router-dom.min";
import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { chLoader } from "../store/action/loaderAction";
import Card from "react-bootstrap/Card";
import Loader from "../Components/loader";
import BtnsCo from "../Components/Btns";
import StarsRating from "../Components/rate";
import Image from "react-bootstrap/Image";

import Carousel from "react-bootstrap/Carousel";

export default function SpecficProd() {
  const loader = useSelector((state) => state.loader.onLoad);
  const dispatch = useDispatch();
  const [speProducts, setSpeProduct] = useState([]);
  const { prodId } = useParams();
  let sessionLogin = JSON.parse(sessionStorage.getItem("login") || "[]");

  const [index, setIndex] = useState(0);

  const handleSelect = (selectedIndex) => {
    setIndex(selectedIndex);
  };

  useEffect(() => {
    axios
      .get(`https://dummyjson.com/products/${prodId}`)
      .then((res) => {
        setTimeout(() => {
          dispatch(chLoader(false));
        }, 2000);
        setSpeProduct(res.data);
      })
      .catch((rej) => {
        console.log("err");
      });
  }, []);
  console.log("test");
  console.log(speProducts.id);

  return (
    <>
      {loader ? (
        <>
          <div className="d-flex justify-content-center align-items-center vh-100">
            <Loader />
          </div>
        </>
      ) : (
        <>
          <p>Test</p>
          <div className="pt-3 d-flex container justify-content-center gap-3 flex-wrap">
            <Carousel fade>
              <Carousel.Item>
                <Image src={`${speProducts.images[0]}`} rounded />
                <Carousel.Caption>
                  <Image src={`${speProducts.images[0]}`} rounded />
                  <p>
                    Nulla vitae elit libero, a pharetra augue mollis interdum.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
              <Carousel.Item>
                <Image src={`${speProducts.images[0]}`} rounded />
                <Carousel.Caption>
                  <h3>Second slide label</h3>
                  <p>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
              <Carousel.Item>
                <Image src={`${speProducts.images[0]}`} rounded />
                <Carousel.Caption>
                  <h3>Third slide label</h3>
                  <p>
                    Praesent commodo cursus magna, vel scelerisque nisl
                    consectetur.
                  </p>
                </Carousel.Caption>
              </Carousel.Item>
            </Carousel>
          </div>
        </>
      )}
    </>
  );
}
