import React, { useEffect, useState } from "react";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import BtnsCo from "../Components/Btns";
import { useDispatch, useSelector } from "react-redux";
import { chLoader } from "../store/action/loaderAction";

function ProductPage() {
  const [product, setProduct] = useState([]);
  const loader = useSelector((state) => state.loader.onLoad);
  const dispatch = useDispatch();
  useEffect(() => {
    axios
      .get("https://dummyjson.com/products")
      .then((res) => {
        dispatch(chLoader(false));
        setProduct(res.data.products);
      })
      .catch((rej) => {
        console.log("err");
      });
  }, []);

  console.log(product.length > 0 ? product[0].title : "Loading...");
  return (
    <>
      {<Card style={{ width: "18rem" }}>
        <Card.Img variant="top" src="holder.js/100px180" />
        <Card.Body>
          <Card.Title>
            {product.length > 0 ? product[0].title : "Loading..."}
          </Card.Title>
          <Card.Text>
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </Card.Text>
          <BtnsCo btnText="Add To Cart" btnCo="danger" />
        </Card.Body>
      </Card>}
    </>
  );
}

export default ProductPage;
