import React, { useEffect, useState } from "react";
import axios from "axios";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import BtnsCo from "../Components/Btns";
import { useDispatch, useSelector } from "react-redux";
import { chLoader } from "../store/action/loaderAction";
import Loader from "../Components/loader";

function ProductPage() {
  const [products, setProduct] = useState([]);
  const loader = useSelector((state) => state.loader.onLoad);
  const dispatch = useDispatch();
  useEffect(() => {
    dispatch(chLoader(true));
    axios
      .get("https://dummyjson.com/products")
      .then((res) => {
        dispatch(chLoader(false));
        setProduct(res.data.products);
      })
      .catch((rej) => {
        console.log("err");
      });
  }, []);

  // console.log(products[0].title);

  return (
    <>
      {loader ? (
        <>
          <div className="d-flex justify-content-center align-items-center vh-100">
            <Loader />
          </div>
        </>
      ) : (
        <Card style={{ width: "18rem" }}>
          {/* <Card.Img variant="top" src={products[0].images[0]} /> */}
          <Card.Body>
            {/* <Card.Title>{products[1].title}</Card.Title> */}
            <Card.Text>
              Some quick example text to build on the card title and make up the
              bulk of the card's content.
            </Card.Text>
            <BtnsCo btnText="Add To Cart" btnCo="danger" />
          </Card.Body>
        </Card>
      )}
    </>
  );
}

export default ProductPage;
